AFMU repairs.script:
{event.item}

{_ ****** BUSCA GENERO Y NUMERO DE event.item ****** _}

{_ coge la primera palabra}
{set itemobj to token(event.item, " ",0)} 

{_ mira si es femenino}
{if find(itemobj,"a",len(itemobj)-2)!=-1: {set o to "a"}
|else: {set o to "o"}
}

{_ mira si es plural}
{if find(itemobj,"s",len(itemobj)-1)!=-1: {set s to "s"}{set n to "n"}
|else: {set s to ""}{set n to ""}
}

{_ ************************************************** _}


{if event.repairedfully:
    totalmente reparad{o}{s}
|else:
    reparada 
    {Occasionally(2, cat(
        OneOf("al ", "en un "),
        Humanise(event.health * 100),
        " porciento de su capacidad"
    ))}
}

{Occasionally(2, 
    cat(OneOf(". ", "y está{n}"), " List{o}{s} para su activación"))}. 
{Occasionally(2,cat(OneOf("No olvide","Recuerde") "activarl{o}"))}

