Bodies to map.script:
{_ Preferences: _}

{set reportTerraformables to true}   {_ Reports all terraformable and terraformed bodies _}
{set highValueThreshold to 80000}   {_ The minimum threshold for estimated exploration value worth mapping _}
{set reportPlanetTypes to [
    "Mundo de amoníaco": true,
    "Mundo similar a la Tierra": true,
    "Gigante gaseoso con vida basada en el amoníaco": false,
    "Gigante gaseoso con vida basada en el agua": false,
    "Gigante gaseoso de Helio": false,
    "Gigante gaseoso rico en Helio": false,
    "Mundo de alto contenido metálico": false,
    "Cuerpo helado": false,
    "Cuerpo con alto contenido metálico": false,
    "Mundo helado rocoso": false,
    "Cuerpo rocoso": false,
    "Gigante gaseoso de Clase 1": false,
    "Gigante gaseoso de Clase 2": false,
    "Gigante gaseoso de Clase 3": false,
    "Gigante gaseoso de Clase 4": false,
    "Gigante gaseoso de Clase 5": false,
    "Gigante acuático": false,
    "Gigante acuático con vida": false,
    "Mundo acuático": true,
]}

{_ Fetch from Context _}
{set reportSystem to SystemDetails(state.eddi_context_system_name)}

{_ Identify bodies worth mapping _}

{set toMapList to []}
{set mappedBodies to 0}
{set totalBodies to 0}

{for body in reportSystem.bodies:
    {set totalBodies to totalBodies + 1}
    {if body.mapped:
        {set mappedBodies to mappedBodies + 1}
    |else:
        {if body.bodyType.invariantName != "Star":
            {if body.terraformState.invariantName = "Candidato para terraformación" 
             || body.terraformState.invariantName = "Terraformado":
                {if reportTerraformables:
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    } 
                }
            |elif reportPlanetTypes[body.planetClass.invariantName]:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                }
            |elif body.estimatedvalue > highValueThreshold:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                } 
            }
        }
    }
}

{_ Begin speaking _}

{if len(toMapList) > 0:

    
    {if len(toMapList) = 1: El cuerpo |else: Los cuerpos}

    {set cur to 0}
    {while cur < len(toMapList):
        {P(toMapList[cur])}
        {set cur to cur + 1}
        {if cur = len(toMapList) - 1: y
        |elif cur < len(toMapList) - 1:,
        }
    }

    {if len(toMapList) = 1: es |else: son}
    {if event.type = "Body mapped": {OneOf("además")} }
    {if len(toMapList) = 1: un }
    {if len(toMapList) = 1: {OneOf("recomendado", "buen")} |else: {OneOf("recomendados", "buenos")}}
    {set candidates to:
        candidato{if len(toMapList) > 1:s}
    }
    {OneOf('{candidates} para mapear', '{candidates} para escanear')}.

|elif mappedBodies > 0:

    {OneOf(
        "Ya has mapeado todos los cuerpos {if mappedBodies < totalBodies: {OneOf("que valen la pena", "interesantes")}}.",
        "Todos los cuerpos {if mappedBodies < totalBodies: {OneOf("que valen la pena", "interesantes")}} {OneOf("han sido mapeados", "ya los tenemos mapeados")}."
    )}
}

