Body atmosphere report.script:
{_ Preferences _}
{set reportAtmoPressure to 1}   {_ configúralo a 1 si quieres escuchar acerca de la presión atmosférica. _}

{set reportAtmoTempScale to 1}  {_ configúralo a 0 si quieres escuchar las temperaturas en Kelvin _}
                                {_ configúralo a 1 si quieres escuchar las temperaturas en Celsius _}
                                {_ configúralo a 2 si quieres escuchar las temperaturas en Fahrenheit _}
                                {_ configúralo a 3 si no quieres escuchar las temperaturas de los cuerpos _}

{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {round(kelvin - 273, 0)}}
{set ToFahrenheit(kelvin) to: {round((kelvin-273)*9/5 + 32 , 0)}}
{set TempCalc(kelvin) to: 
    {if reportAtmoTempScale = 0: {kelvin}}
    {if reportAtmoTempScale = 1: {ToCelsius(kelvin)}}
    {if reportAtmoTempScale = 2: {ToFahrenheit(kelvin)}}
}

{_ Name our temperature scale _}
{if reportAtmoTempScale = 0: {set tempscale to: Kelvin}}
{if reportAtmoTempScale = 1: {set tempscale to: grados centígrados}}
{if reportAtmoTempScale = 2: {set tempscale to: grados Fahrenheit}}

{_ Fetch from context }
{if event: 
    {set reportbody to event} 
}
{if !event.name || event.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
}
{if !reportbody.name || reportbody.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    Lo siento, no me queda claro a que cuerpo estelar se refiere

|else:

    {if find(reportbody.planettype, "Gigante") >= 0:
        {if find(reportbody.planettype, "Clase") >= 0: 
            {set sudarsky to Occasionally(3, "Sud-dar-eskai")}
        }
        {set planetdesc to OneOf("Los gigantes gaseosos {sudarsky} como este, tienen", "Este gigante gaseoso {sudarsky} tiene ")}
        {if find(reportbody.planettype, "Clase 5") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(1373)}}
                temperaturas por encima de {Humanise(tempmin)} {tempscale}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("hierro y silicatos en forma de vapor", "vapores de hierro y silicatos")}.
        |elif find(reportbody.planettype, "Clase 4") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(354)}}
                {set tempmax to: {TempCalc(903)}}
                temperaturas {OneOf("de", "que oscilan")} entre {Humanise(tempmin)} y {Humanise(tempmax)} {tempscale}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("vapores metálicos alcalinos", "metales alcalinos en forma de vapor")}.   
        |elif find(reportbody.planettype, "Clase 3") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(251)}}
                {set tempmax to: {TempCalc(353)}}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre {Humanise(tempmin)} y {Humanise(tempmax)} {tempscale},
            }
            una temperatura a la que {OneOf("ningún elemento gaseoso forma nubes", "no hay químicos adecuados para que en la atmósfera se pueda sustentar una capa de nubes", "a la atmósfera no se sostiene ninguna capa persistente de nubes")}.

        |elif find(reportbody.planettype, "Clase 2") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(154)}}
                {set tempmax to: {TempCalc(250)}}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre los
  
            {if find(tempmin, "-") >= 0: {tempmin*-1}
            |else: {Humanise(tempmin)}
            } y

            {if find(tempmax, "-") >= 0: {tempmax*-1} {tempscale} bajocero}
            |else: {Humanise(tempmax)}{tempscale}
            }, 
            
            {OneOf("con", "formando")} una densa capa de nubes de vapor de agua.  

        |elif find(reportbody.planettype, "Clase 1") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(153)}}
                {set tempmax to: {TempCalc(83)}}
                temperaturas de superficie de 
            
            {if find(tempmax, "-") >= 0: {tempmax*-1} {tempscale} bajocero}
            |else: {Humanise(tempmax)}{tempscale}
            } 
            {Occasionally(2, OneOf(" o menos", "como máximo"))}, {OneOf("y", "con", "formando")}
             
             una densa capa de nubes de amoníaco.  
        }

    |elif reportbody.atmosphere = "Sin atmósfera" || !reportbody.atmosphere:
        {Occasionally(4, OneOf("No hay atmósfera.", "está carente de cualquier tipo de atmósfera.", "los años han limpiado cualquier rastro de atmósfera."))}

        {if reportbody.temperature && reportAtmoTempScale < 3:
            Las temperaturas de superficie se mueven en los
            {set temp to: {TempCalc(reportbody.temperature)}}
            
            {if find(temp, "-")>=0: {temp*-1} {tempscale} bajocero}}
            {if find(temp, "-")=-1: {temp} {tempscale}}
        .

    |elif reportbody.pressure: 
        {if reportbody.pressure < 0.1:
            {set pressdesc to OneOf("casi inapreciable", "extremadamente delgada")}
        |elif reportbody.pressure < 0.25:
            {set pressdesc to "muy delgada"}
        |elif reportbody.pressure < 0.5:
            {set pressdesc to "delgada"}
        |elif reportbody.pressure < 2:
            {set pressdesc to "afable"}
        |elif reportbody.pressure < 10:
            {set pressdesc to "amplia"}
        |elif reportbody.pressure < 50:
            {set pressdesc to OneOf("densa", "muy amplia")}
        |elif reportbody.pressure < 100:
            {set pressdesc to OneOf("muy densa", "extremadamente amplia")}
        |elif reportbody.pressure >= 100:
            {set pressdesc to OneOf("aplastante", "extremadamente densa")}
        }
        {if find(reportbody.atmosphere, "Rico") >= 0:
            {set element to token(reportbody.atmosphere, " ", "2")}
            {set desc to OneOf("Se ha detectado una ", "Hay una ", "Los sensores indican de la presencia de una")}
            {OneOf("{desc} {pressdesc} atmósfera {reportbody.atmosphere}", "La atmósfera de este planeta es {pressdesc} y {reportbody.atmosphere}", "La {pressdesc} atmósfera de este planeta es rica en {element}")}

        |elif find(atmo, "vapor") >= 0:
            {OneOf("Su {pressdesc} atmósfera es de {reportbody.atmosphere}", "Su atmósfera es {pressdesc} y está compuesta de {reportbody.atmosphere}", "Su {pressdesc} atmósfera se compone de {reportbody.atmosphere}")}

        |else:
            {set mostly to OneOf("principalmente", "sobre todo", "esencialmente")}
            {OneOf("La atmósfera de este planeta es {pressdesc}, y su composición es {mostly}", "Este planeta tiene una {pressdesc} atmósfera compuesta")} 
            de {reportbody.atmosphere},
        }
        { if reportAtmoPressure > 0:
            con una presión en superficie de {round(reportbody.pressure,1)} atmósferas terrestres,
        }
        {if reportbody.temperature && reportAtmoTempScale < 3:
            {set temp to: {TempCalc(reportbody.temperature)}}
            y una media de temperatura en superficie de 
            {if find(temp, "-")>=0: {temp*-1} {tempscale} bajocero}}
            {if find(temp, "-")=-1: {temp} {tempscale}}
    }
}.

Body atmosphere report.defaultValue:
{_ Preferences _}
{set reportAtmoPressure to 1}   {_ configúralo a 1 si quieres escuchar acerca de la presión atmosférica. _}

{set reportAtmoTempScale to 1}  {_ configúralo a 0 si quieres escuchar las temperaturas en Kelvin _}
                                {_ configúralo a 1 si quieres escuchar las temperaturas en Celsius _}
                                {_ configúralo a 2 si quieres escuchar las temperaturas en Fahrenheit _}
                                {_ configúralo a 3 si no quieres escuchar las temperaturas de los cuerpos _}

{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {round(kelvin - 273, 0)}}
{set ToFahrenheit(kelvin) to: {round((kelvin-273)*9/5 + 32 , 0)}}
{set TempCalc(kelvin) to: 
    {if reportAtmoTempScale = 0: {kelvin}}
    {if reportAtmoTempScale = 1: {ToCelsius(kelvin)}}
    {if reportAtmoTempScale = 2: {ToFahrenheit(kelvin)}}
}

{_ Name our temperature scale _}
{if reportAtmoTempScale = 0: {set tempscale to: Kelvin}}
{if reportAtmoTempScale = 1: {set tempscale to: grados centígrados}}
{if reportAtmoTempScale = 2: {set tempscale to: grados Fahrenheit}}

{_ Fetch from context }
{if event: 
    {set reportbody to event} 
}
{if !event.name || event.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
}
{if !reportbody.name || reportbody.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    Lo siento, no me queda claro a que cuerpo estelar se refiere

|else:

    {if find(reportbody.planettype, "Gigante") >= 0:
        {if find(reportbody.planettype, "Clase") >= 0: 
            {set sudarsky to Occasionally(3, "Sud-dar-eskai")}
        }
        {set planetdesc to OneOf("Los gigantes gaseosos {sudarsky} como este, tienen", "Este gigante gaseoso {sudarsky} tiene ")}
        {if find(reportbody.planettype, "Clase 5") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(1373)}}
                temperaturas por encima de {Humanise(tempmin)} {tempscale}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("hierro y silicatos en forma de vapor", "vapores de hierro y silicatos")}.
        |elif find(reportbody.planettype, "Clase 4") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(354)}}
                {set tempmax to: {TempCalc(903)}}
                temperaturas {OneOf("de", "que oscilan")} entre {Humanise(tempmin)} y {Humanise(tempmax)} {tempscale}, {OneOf("y", "con", "formando")}
            }
            una densa capa de nubes de {OneOf("vapores metálicos alcalinos", "metales alcalinos en forma de vapor")}.   
        |elif find(reportbody.planettype, "Clase 3") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(251)}}
                {set tempmax to: {TempCalc(353)}}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre {Humanise(tempmin)} y {Humanise(tempmax)} {tempscale},
            }
            una temperatura a la que {OneOf("ningún elemento gaseoso forma nubes", "no hay químicos adecuados para que en la atmósfera se pueda sustentar una capa de nubes", "a la atmósfera no se sostiene ninguna capa persistente de nubes")}.

        |elif find(reportbody.planettype, "Clase 2") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(154)}}
                {set tempmax to: {TempCalc(250)}}
                temperaturas de superficie {OneOf("de", "que oscilan")} entre los
  
            {if find(tempmin, "-") >= 0: {tempmin*-1}
            |else: {Humanise(tempmin)}
            } y

            {if find(tempmax, "-") >= 0: {tempmax*-1} {tempscale} bajocero}
            |else: {Humanise(tempmax)}{tempscale}
            }, 
            
            {OneOf("con", "formando")} una densa capa de nubes de vapor de agua.  

        |elif find(reportbody.planettype, "Clase 1") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(153)}}
                {set tempmax to: {TempCalc(83)}}
                temperaturas de superficie de 
            
            {if find(tempmax, "-") >= 0: {tempmax*-1} {tempscale} bajocero}
            |else: {Humanise(tempmax)}{tempscale}
            } 
            {Occasionally(2, OneOf(" o menos", "como máximo"))}, {OneOf("y", "con", "formando")}
             
             una densa capa de nubes de amoníaco.  
        }

    |elif reportbody.atmosphere = "Sin atmósfera" || !reportbody.atmosphere:
        {Occasionally(4, OneOf("No hay atmósfera.", "está carente de cualquier tipo de atmósfera.", "los años han limpiado cualquier rastro de atmósfera."))}

        {if reportbody.temperature && reportAtmoTempScale < 3:
            Las temperaturas de superficie se mueven en los
            {set temp to: {TempCalc(reportbody.temperature)}}
            
            {if find(temp, "-")>=0: {temp*-1} {tempscale} bajocero}}
            {if find(temp, "-")=-1: {temp} {tempscale}}
        .

    |elif reportbody.pressure: 
        {if reportbody.pressure < 0.1:
            {set pressdesc to OneOf("casi inapreciable", "extremadamente delgada")}
        |elif reportbody.pressure < 0.25:
            {set pressdesc to "muy delgada"}
        |elif reportbody.pressure < 0.5:
            {set pressdesc to "delgada"}
        |elif reportbody.pressure < 2:
            {set pressdesc to "afable"}
        |elif reportbody.pressure < 10:
            {set pressdesc to "amplia"}
        |elif reportbody.pressure < 50:
            {set pressdesc to OneOf("densa", "muy amplia")}
        |elif reportbody.pressure < 100:
            {set pressdesc to OneOf("muy densa", "extremadamente amplia")}
        |elif reportbody.pressure >= 100:
            {set pressdesc to OneOf("aplastante", "extremadamente densa")}
        }
        {if find(reportbody.atmosphere, "Rico") >= 0:
            {set element to token(reportbody.atmosphere, " ", "2")}
            {set desc to OneOf("Se ha detectado una ", "Hay una ", "Los sensores indican de la presencia de una")}
            {OneOf("{desc} {pressdesc} atmósfera {reportbody.atmosphere}", "La atmósfera de este planeta es {pressdesc} y {reportbody.atmosphere}", "La {pressdesc} atmósfera de este planeta es rica en {element}")}

        |elif find(atmo, "vapor") >= 0:
            {OneOf("Su {pressdesc} atmósfera es de {reportbody.atmosphere}", "Su atmósfera es {pressdesc} y está compuesta de {reportbody.atmosphere}", "Su {pressdesc} atmósfera se compone de {reportbody.atmosphere}")}

        |else:
            {set mostly to OneOf("principalmente", "sobre todo", "esencialmente")}
            {OneOf("La atmósfera de este planeta es {pressdesc}, y su composición es {mostly}", "Este planeta tiene una {pressdesc} atmósfera compuesta")} 
            de {reportbody.atmosphere},
        }
        { if reportAtmoPressure > 0:
            con una presión en superficie de {round(reportbody.pressure,1)} atmósferas terrestres,
        }
        {if reportbody.temperature && reportAtmoTempScale < 3:
            {set temp to: {TempCalc(reportbody.temperature)}}
            y una media de temperatura en superficie de 
            {if find(temp, "-")>=0: {temp*-1} {tempscale} bajocero}}
            {if find(temp, "-")=-1: {temp} {tempscale}}
    }
}.

