Body materials report.script:
{_ Preferences _}
{set reportMats to 2} {_ Pon 0 si quieres escuchar los materiales que tienes por debajo de tu nivel mínimo de inventario _}
                      {_ Pon 1 si quieres escuchar los materiales que tienes por debajo del nivel deseado _}
                      {_ Pon 2 si quieres escuchar todos los materiales del cuerpo estelar _}

{set reportMatConc to 2} {_ Pon 0 si no quieres escuchar los % de los materiales _}
                         {_ Pon 1 si sólo deseas escuchar los % altos de materiales _}
                         {_ Pon 2 si quieres escuchar desde los % buenos de materiales _}
                         {_ Pon 3 si quieres escuchar todos los % de materiales _}


{_ Fetch from context }
{if event:
    {set reportbody to event} 
}
{if !event.name || event.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
}
{if !reportbody.name || reportbody.name = "": 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{if !reportbody.name || reportbody.name = "":
    {_ si todavía no se puede obtener el nombre del cuerpo...  _}
    {Occasionally(2,"Lo siento. ")}
    {OneOf("Hay interferencias en el sistema electrónico, y no consigo resultados óptimos en el escaneo de este cuerpo estelar",
           "No consigo datos claros del escáner",
           "Los datos del escáner se han corrompido antes de su recepción",
           "Parece que hay interferencias solares que no nos permiten el correcto escaneo de este planeta"
    )}


|else:
    {_ Define a function to obtain a material from inventory}
    {set _inventory(required) to:
        {for material in materials:
            {if material.material = required:
                {return material}
            }
        }
    }

    {if reportbody.landable:

        {set goodmaterials to []}
        {set greatmaterials to []}
        {set allmaterials to []}
        {for material in reportbody.materials:
            {set inventoryMat to _inventory(material.material)}
            {if (reportMats >= 0 && inventoryMat.amount < inventoryMat.minimum && inventoryMat.minimum > 0)
              || (reportMats >= 1 && inventoryMat.amount < inventoryMat.desired && inventoryMat.desired > 0)
              || (reportMats >= 2):
                {if material.percentage >= material.definition.greatpctbody:
                    {set greatmaterials to cat(greatmaterials, [material])}
                |elif material.percentage >= material.definition.goodpctbody:
                    {set goodmaterials to cat(goodmaterials, [material])}
                |else:
                    {set allmaterials to cat(allmaterials, [material])}
                }
            }
        }

        {if len(goodmaterials) > 0 || len(greatmaterials) > 0 || len(allmaterials) > 0:
            {if !body:
                Este planeta {F("Body Name Tidy")}
                {body}
            |else:
                Este cuerpo
            }
            contiene

            {if len(greatmaterials) > 0:
                niveles muy altos 
                {set cur to 0}
                {set length to len(greatmaterials) - 1} de
                {while cur < len(greatmaterials):
                    {P(greatmaterials[cur].definition.name)}
                    {if reportMatConc > 0: en un {round(greatmaterials[cur].percentage, 1)}% }
                    {set cur to cur + 1}
                    {if cur = length: y de
                    |elif cur < length:,
                    }
                }
            }

            {if len(goodmaterials) > 0:
                {if len(greatmaterials) > 0: y }
                altos niveles
                {set cur to 0}
                {set length to len(goodmaterials) - 1} de
                {while cur < len(goodmaterials):
                    {P(goodmaterials[cur].definition.name)}
                    {if reportMatConc > 1: en un {round(goodmaterials[cur].percentage, 1)}% }
                    {set cur to cur + 1}
                    {if cur = length: y de
                    |elif cur < length:,
                    }
                }
            }

            {if len(greatmaterials) > 0 || len(goodmaterials) > 0:. }

            {if len(allmaterials) > 0 && reportMats >= 2:
                {if len(greatmaterials) > 0 || len(goodmaterials) > 0:
                    Además, {OneOf("puede que encuentre","el escaner también detecta")} algo
                |else:
                    niveles considerables 
                }
                {set cur to 0}
                {set length to len(allmaterials) - 1} de 
                {while cur < len(allmaterials):
                    {P(allmaterials[cur].definition.name)}
                    {if reportMatConc > 2: en un {round(allmaterials[cur].percentage, 1)}% }
                    {set cur to cur + 1}
                    {if cur = length: y de
                    |elif cur < length:,
                    }
                }.
            }
        }
    }
}

