Body report summary.script:
{_ Preferences _}

{set reportTerraformables to 1}         {_ Reports terraformable and terraformed bodies _}
{set reportPlanetTypes to 1}            {_ Reports Earth-like and water worlds _}
{set reportMass to 1}                   {_ Reports notable masses (relative to Earth's) _}
{set reportRadius to 1}                 {_ Reports notable radii (in kilometers) _}
{set reportDensity to 0}                {_ Reports notable densities (in kilograms per cubic meter) _}
{set reportGravity to 1}                {_ Reports notable gravity (in G's) for landable bodies _}
{set reportTemperature to 1}            {_ Reports notable temperatures (in Kelvin) _}
{set reportMaterials to 1}              {_ Reports materials per preferences in the `Body materials` script. _}
{set reportLandableVolcanism to 0}      {_ Reports volcanism on landable worlds _}
{set reportRings to 1}                  {_ Reports ring details _}
{set reportInterestingOrbits to 1}      {_ Reports notable orbital characteristics _}
{set reportRapidRotation to 1}          {_ Reports very fast rotating bodies _}

{set reportAtmoTempScale to 1}  {_ configúralo a 0 si quieres escuchar las temperaturas en Kelvin _}
                                {_ configúralo a 1 si quieres escuchar las temperaturas en Celsius _}

{_ temp conversion _}
{if reportAtmoTempScale = 0: {set tempnum to: {Humanise(reportbody.temperature)}}}
{if reportAtmoTempScale = 1: {set tempnum to: {Humanise(reportbody.temperature - 273,15)}}}

{_ Name our temperature scale _}
{if reportAtmoTempScale = 0: {set tempscale to: Kelvin}}
{if reportAtmoTempScale = 1: {set tempscale to: grados centígrados}}



{_ Fetch from context _}
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody: 
    {set reportbody to BodyDetails(state.eddi_context_body_name)}
}

{_ Begin processing _}

{set notables to []}

{if reportInterestingOrbits:

    {set orbitNotables to []}

    {if reportbody.eccentricityprobability > 99.9:
        {set orbitNotables to cat(orbitNotables, [cat("increiblemente ", OneOf("elíptico", "ovalado", "alargado"))])}
    |elif reportbody.eccentricityprobability > 99:
        {set orbitNotables to cat(orbitNotables, [cat("extremadamente ", OneOf("elliptical", "oblong", "elongated"))])}
    |elif reportbody.eccentricityprobability > 97:
        {set orbitNotables to cat(orbitNotables, [cat("inusualmente ", OneOf("elliptical", "oblong", "elongated"))])}
    }

    {if reportbody.inclinationprobability > 99.9:
        {set orbitNotables to cat(orbitNotables, [cat("increiblemente inclinado")])}
    |elif reportbody.inclinationprobability > 99:
        {set orbitNotables to cat(orbitNotables, [cat("extremadamente inclinado")])}
    |elif reportbody.inclinationprobability > 97:
        {set orbitNotables to cat(orbitNotables, [cat("inusualmente inclinado")])}
    }

    {if reportbody.orbitalperiod < 0: {set retrograde to cat(" ", "retrógrado", " ")} }
    {if reportbody.orbitalperiodprobability < 0.1:
        {set orbitNotables to cat(orbitNotables, [cat("increiblemente rápido", retrograde)])}
    |elif reportbody.orbitalperiodprobability < 1:
        {set orbitNotables to cat(orbitNotables, [cat("extremadamente rápido", retrograde)])}
    |elif reportbody.orbitalperiodprobability < 3:
        {set orbitNotables to cat(orbitNotables, [cat("inusualmente rápido", retrograde)])}
    }

    {if len(orbitNotables) > 0:
        {if StartsWithVowel(orbitNotables[0]): {set a to "un"} |else: {set a to "un"} }
        {set cur to 0}
        {set length to len(orbitNotables) - 1}
        {while cur < len(orbitNotables):
            {set orbitdescription to cat(orbitdescription, orbitNotables[cur])}
            {set cur to cur + 1}
            {if cur = length: 
                {set orbitdescription to cat(orbitdescription, " y ")}
            |elif cur < length:
                {set orbitdescription to cat(orbitdescription, ", ")}
            }
        }
        {set notables to cat(notables, [cat(a, " ", orbitdescription, " ", "órbita")])}
    }
}

{if reportbody.rotationalperiod < 0: {set retrograde to cat(" ", "retrógrado", " ")} }
{if reportRapidRotation:
    {if !retrograde && reportbody.rotationalperiodprobability < 0.1:
        {set notables to cat(notables, [cat("un período de rotación increíblemente corto de ", Humanise(reportbody.rotationalperiod), " días")])}
    |elif !retrograde && reportbody.rotationalperiodprobability < 1:
        {set notables to cat(notables, [cat("un período de rotación extremadamente corto de ", Humanise(reportbody.rotationalperiod), " días")])}
    |elif !retrograde && reportbody.rotationalperiodprobability < 3:
        {set notables to cat(notables, [cat("un período de rotación inusualmente corto de ", Humanise(reportbody.rotationalperiod), " días")])}
    }
}

{if reportMass:
    {if reportbody.massprobability < 0.1:
        {set notables to cat(notables, [cat("una masa increíblemente baja de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    |elif reportbody.massprobability < 1:
        {set notables to cat(notables, [cat("una masa extremadamente baja de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    |elif reportbody.massprobability < 3:
        {set notables to cat(notables, [cat("una masa inusualmente baja de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    |elif reportbody.massprobability > 99.9:
        {set notables to cat(notables, [cat("una masa increíblemente alta de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    |elif reportbody.massprobability > 99:
        {set notables to cat(notables, [cat("una masa extremadamente alta de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    |elif reportbody.massprobability > 97:
        {set notables to cat(notables, [cat("una masa inusualmente alta de ", Humanise(reportbody.earthmass), " veces la de la Tierra")])}
    }
}

{if reportRadius:
    {if reportbody.radiusprobability < 0.1:
        {set notables to cat(notables, [cat("un radio increíblemente bajo de ", Humanise(reportbody.radius), " kilómetros")])}
    |elif reportbody.radiusprobability < 1:
        {set notables to cat(notables, [cat("un radio extremadamente bajo de ", Humanise(reportbody.radius), " kilómetros")])}
    |elif reportbody.radiusprobability < 3:
        {set notables to cat(notables, [cat("un radio inusualmente bajo de ", Humanise(reportbody.radius), " kilómetros")])}
    |elif reportbody.radiusprobability > 99.9:
        {set notables to cat(notables, [cat("un radio extremadamente alto de ", Humanise(reportbody.radius), " kilómetros")])}
    |elif reportbody.radiusprobability > 99:
        {set notables to cat(notables, [cat("un radio increíblemente largo de ", Humanise(reportbody.radius), " kilómetros")])}
    |elif reportbody.radiusprobability > 97:
        {set notables to cat(notables, [cat("un radio inusualmente grande de ", Humanise(reportbody.radius), " kilómetros")])}
    }
}

{if reportTemperature:
    {if reportbody.tempprobability < 0.1:
        {set notables to cat(notables, [cat("una temperatura superficial increíblemente baja de ", tempnum, tempscale)])}
    |elif reportbody.tempprobability < 1:
        {set notables to cat(notables, [cat("una temperatura superficial extremadamente baja de ", tempnum, tempscale)])}
    |elif reportbody.tempprobability < 3:
        {set notables to cat(notables, [cat("una temperatura superficial inusualmente baja de ", tempnum, tempscale)])}
    |elif reportbody.tempprobability > 99.9:
        {set notables to cat(notables, [cat("una temperatura superficial extremadamente alta de ", tempnum, tempscale)])}
    |elif reportbody.tempprobability > 99:
        {set notables to cat(notables, [cat("una temperatura superficial increiblemente alta de ", tempnum, tempscale)])}
    |elif reportbody.tempprobability > 97:
        {set notables to cat(notables, [cat("una temperatura superficial inusualmente alta de ", tempnum, tempscale)])}
    }
}

{if reportGravity && reportbody.landable:
    {if reportbody.gravityprobability < 0.1:
        {set notables to cat(notables, [cat("una gravedad increíblemente baja de ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    |elif reportbody.gravityprobability < 1:
        {set notables to cat(notables, [cat("una gravedad extremadamente baja de  ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    |elif reportbody.gravityprobability < 3:
        {set notables to cat(notables, [cat("una gravedad inusualmente baja de  ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    |elif reportbody.gravityprobability > 99.9:
        {set notables to cat(notables, [cat("una gravedad increíblemente alta de ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    |elif reportbody.gravityprobability > 99:
        {set notables to cat(notables, [cat("una gravedad extremadamente alta de ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    |elif reportbody.gravityprobability > 97:
        {set notables to cat(notables, [cat("una gravedad inusualmente alta de ", Humanise(reportbody.gravity), " veces la de la tierra")])}
    }
}


{if reportDensity:
    {if reportbody.densityprobability < 0.1:
        {set notables to cat(notables, [cat("una densidad increíblemente baja ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    |elif reportbody.densityprobability < 1:
        {set notables to cat(notables, [cat("una densidad extremadamente baja ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    |elif reportbody.densityprobability < 3:
        {set notables to cat(notables, [cat("una densidad inusualmente baja ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    |elif reportbody.densityprobability > 99.9:
        {set notables to cat(notables, [cat("una densidad increíblemente alta ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    |elif reportbody.densityprobability > 99:
        {set notables to cat(notables, [cat("una densidad extremadamente alta ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    |elif reportbody.densityprobability > 97:
        {set notables to cat(notables, [cat("una densidad inusualmente alta ", Humanise(reportbody.density), " kilogramos por metro cúbico")])}
    }
}

{if reportVolcanism:
    {if reportbody.volcanism && reportbody.landable:
        {set notables to cat(notables, [cat(OneOf("volcánica", "tectónica"), "actividad")])}
    }
}

{if reportRings:
    {if reportbody.rings:
        {set ringcompositions to []}
        {set cur to 0}
        {for ring in reportbody.rings:
            {if find(ring.name, "Anillo") > -1:
                {if find(ringcompositions, ring.composition) = -1:
                    {set ringcompositions to cat(ringcompositions, [cat(ring.composition)])}
                }
            }
            {set cur to cur + 1}
        }
        {set cur to 0}
        {set length to len(ringcompositions) - 1}
        {while cur < len(ringcompositions):
            {set ringsdescription to cat(ringsdescription, ringcompositions[cur])}
            {set cur to cur + 1}
            {if cur = length: 
                {set ringsdescription to cat(ringsdescription, " y ")}
            |elif cur < length:
                {set ringsdecription to cat(ringsdescription, ", ")}
            }
        }
        {if ringsdescription:
            {if len(ringcompositions) = 1:
                {if StartsWithVowel(reportbody.reserves): 
                    {set a to "un "} 
                |else: 
                    {set a to "un "}
                }
                {set rings to "anillo"}
            |else:
                {set rings to "anillos"} 
            }
            {if reportbody.reserveLevel.invariantName != "Ninguno": 
                {set reserves to reportbody.reserves}
            }
            {set notables to cat(notables, [cat(a, " ", reserves, " ", ringsdescription, " ", rings)])}
        }
    }
}

{if reportTerraformables:
    {if reportbody.terraformState.invariantName = "Candidato para terraformación":
        {set notables to cat(notables, [OneOf("potencial de terraformación", "condiciones terraformables")])}
    |elif reportbody.terraformState.invariantName = "Terraformed":
        {set notables to cat(notables, ["condiciones terraformadas"])}
    }
}

{if reportPlanetTypes:
    {if reportbody.planetClass.invariantName = "Mundo similar a la Tierra":
        {set notables to cat(notables, ["Mundo similar a la Tierra"])}
    |elif reportbody.planetClass.invariantName = "Mundo acuático":
        {set notables to cat(notables, [OneOf("abundante agua líquida", "abundante agua líquida en la superficie")])}
    }
}

{_ Begin speaking _}

{if !reportbody.name || reportbody.name = "":

    No tengo claro a que cuerpo estelar se refiere.

|else:

    {if len(notables) > 0:
        {if reportbody.shortname != reportbody.bodyname:
            {if reportbody.bodytype:
                {reportbody.bodytype}
            |else:
                Cuerpo
            }
            {P(reportbody.shortname)}
        |else:
            {P(reportbody.bodyname)}
        }
        is

        {if len(notables) = 1:
            {set desc to "notable"}
        |elif len(notables) = 2:
            {set desc to OneOf('interesante', 'notable', 'remarcable')}
        |elif len(notables) = 3:
            {set desc to OneOf('excepcional', 'remarcable')}
        |elif len(notables) = 4:
            {set desc to OneOf('extraordinario')}
        |elif len(notables) >= 5:
            {set desc to OneOf('sin par', 'único')}
        }
        {OneOf("{desc},", "{if StartsWithVowel(desc): un |else: un } {desc} {P(reportbody.planettype)}")}

        {if len(notables) = 1:
            con {notables[0]}.
        |elif len(notables) = 2:
            con {notables[0]} y {notables[1]}.
        |elif len(notables) = 3:
            con {notables[0]}, {notables[1]} y {notables[2]}.
        |elif len(notables) = 4:
            con {notables[0]}, {notables[1]}, {notables[2]}, y {notables[3]}.
        |elif len(notables) >= 5:
            con {notables[0]}, {notables[1]}, {notables[2]}, {notables[3]}, y {notables[4]}.
        }
    }

    {if reportMaterials:
        {F("Body materials report")}
    }
}

