Community goal.script:
{_ Set Context _}
{SetState('eddi_context_last_subject', 'community goal')}
{_ Save goal location to context if there is only one goal present _}
{if len(event.cgid) = 1:
   {SetState('eddi_context_goal_system_name', event.system[0])}
   {SetState('eddi_context_goal_station_name', event.station[0])}
}

{F('Honorific')}, 

{if len(event.name) > 1:
    {OneOf("ha expresado su interés en", "estoy siguiendo")}
    los siguientes eventos de la comunidad:
|else: 
    El evento de la comunidad,    
}

{set cur to 0}
{while cur < len(event.name):

   {if cur = 0 && len(event.name) = 1:
      {_ nothing _}
   |elif cur = 0 && len(event.name) > 1:
      El primero,
   |elif cur < (len(event.name) - 1):
      el siguiente, 
   |else:
      el último,
   }

   {event.name[cur]},

   {if event.iscomplete[cur]:
      {OneOf("está completado", "ha finalizado")} 
   |else: 
      {if (event.expiry[cur] / 3600) < 12:
         {OneOf("está a punto de terminar", 
                "terminará en {round(event.expiry[cur] / 3600, 1)} horas", 
                "está casi completado")}
      |else:
         {OneOf("no se ha completado aun,", 
                "está activo", 
                "está por terminar")} 
      }
   }

   {if event.tier[cur]:
       {OneOf("y ha alcanzado el", "y está ahora mismo en el")} {event.tier[cur]}.
       {if event.contribution[cur] > 0:
           Su recompensa asignada está
