Crime check station.script:
{_ Crime check station }
{_ Check claims, fines & bounties related to the current station }


{set CheckPad(current_station) to:
    {set largest_pad to lcase(current_station.largestpad)}
    {if largest_pad != "Ninguno":
        {if largest_pad = "Grande":
            {return true}
        |elif largest_pad = "Mediano":
            {if lcase(ship.size) = "Grande":
                {return false}
            |else:
                {return true}
            }
        }
        {if lcase(ship.size) = "Pequeño":
            {return true}
        }
    }
    {return false}
}

{set HasService(service) to:
    {if CheckPad(current_station):
        {if find(current_station.stationservices, service) > -1:
            {return true}
        |else:
            {return false}
        }
    |else: {return false}
    }
}

{if state.eddi_context_body_type = 'Estación' && station.name && station.name != "":
    {set current_station to StationDetails(station.name)}
    {set factions_claims to []}
    {set factions_fines to []}
    {set factions_bounties to []}
    {set station_report to false}
    {set system_claims to 0}
    {set system_fines to 0}
    {set system_bounties to 0}

    {for record in criminalrecord:
        {for faction in system.factions:
            {if record.faction = faction.name:

                {if record.claims > 0:
                    {set factions_claims to cat(factions_claims, [record.faction])}
                    {set system_claims to system_claims + record.claims}
                }

                {set reports to record.finesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set system_fines to system_fines + report.amount}
                            {if find(factions_fines, record.faction) < 0:
                                {set factions_fines to cat(factions_fines, [record.faction])}
                            }
                        }
                    }
                }

                {set reports to record.bountiesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set system_bounties to system_bounties + report.amount}
                            {if find(factions_bounties, record.faction) < 0:
                                {set factions_bounties to cat(factions_bounties, [record.faction])}
                            }
                        }
                    }
                }
            }
        }
    }

    {set claim_count to len(factions_claims)}
    {set fine_count to len(factions_fines)}
    {set bounty_count to len(factions_bounties)}
    {set bounties to system_bounties > 0 && HasService("Contacto de Interstellar Factors")}
    {set fines to system_fines > 0 && HasService("Contactos")}
    {set claims to system_claims > 0 && HasService("Contactos")}
    
    {if bounties || fines || claims:
        {OneOf("Tenemos","Hay")} instalaciones disponibles en esta estación para
        {if bounties || fines: limpiar 
            {if bounties: {if bounty_count = 1: el vale |else: los vales} {if fines: y } }
            {if fines: {if fine_count = 1: la multa |else: las multas} } 
            pendiente{if fine_count > 1:s}{if bounty_count > 1:s}{if fines && bounties:s}
        }
        {if bounties || fines: y }
        {if claims: {OneOf("reclamar","cobrar")} tu
            {if claim_count = 1: recompensa pendiente|else:s recompensas pendientes}
            por los servicios prestados
        }.
    }
}

