Crime check system.defaultValue:
{_ Crime check system }
{_ Check claims, fines & bounties related to the current system }


{set CheckPad(station) to:
    {set largest_pad to lcase(station.largestpad)}
    {if largest_pad != "ninguno":
        {if largest_pad = "grande": {return true} }
        |elif largest_pad = "mediano":
            {if lcase(ship.size) = "grande": {return false}
            |else: {return true}
        }
        {if lcase(ship.size) = "pequeño": {return true}
    }
    {return false}
}}

{set NearestStation(service) to:
    {set distance to 0}
    {set station_count to 0}
    {for station in system.stations:
        {if CheckPad(station):
            {if find(station.stationservices, service) > -1:
                {if distance = 0 || station.distancefromstar < distance:
                    {set station_count to station_count + 1}
                    {set distance to station.distancefromstar}
                    {set nearest_station to station.name}
                }
            }
      
  
 
        }
       
    }
}

                  

{set factions_claims to []}
      
{set factions_fines to []}
        
{set factions_bounties to []}

{set system_claims to 0}
{set system_fines to 0}
{set system_bounties to 0}
{set wanted_count to 0}
{set station_report to false}

{for record in criminalrecord:
    {if record.bounties > 0:
        {set factions_bounties to cat(factions_bounties, [record.faction])}
    }
    {for faction in system.factions:
        {if record.faction = faction.name:

            {if record.claims > 0:
                {set factions_claims to cat(factions_claims, [record.faction])}
                {set system_claims to system_claims + record.claims}
            }
            {if record.fines > 0 || record.bounties > 0:
                {set wanted_count to wanted_count + 1}
                {set system_fines to system_fines + record.fines}
                {set system_bounties to system_bounties + record.bounties}
                {if record.fines > 0:
                    {set factions_fines to cat(factions_fines, [record.faction])}
                }
            }
        }
    }
}

{set claim_count to len(factions_claims)}
{set fine_count to len(factions_fines)}
{set bounty_count to len(factions_bounties)}

{if wanted_count > 0:
    Atención Comandante: Está buscado en este sistema por {wanted_count}
    facción{if wanted_count > 1:es},
    que entre multas y recompensas el valor de su cabeza asciende a {Humanise(system_fines + system_bounties)} créditos.
}

{_ Report nearest station to clear bounties }
{if bounties > 0:
    {NearestStation("Contacto de Interstellar Factors")}
    {if station_count > 0:
        Le informo que en {station_count} estación{if station_count > 1:es} de este sistema hay instalaciones legales 
         para poder cobrar su{if bounty_count > 1:s} recompensa{if bounty_count > 1:s} pendiente{if bounty_count > 1:s}.
        La más cercana és la estación {nearest_station}, a {round(distance, 1)} segundos luz de la estrella principal.
      
        {set station_report to true}
    }
}

{if system_fines > 0 && !station_report:
    {NearestStation("Contactos")}
    {if station_count > 0:
        Hay {station_count} estación{if station_count > 1:es} donde poder
  reclamar su{if claim_count > 1:s} recompensa{if claim_count > 1:s} pendiente{if claim_count > 1:s}.
        La más cercana es la estación {nearest_station}, a {round(distance, 1)} segundos luz de la estrella principal.
        {set station_report to true}
    }
}
     
      
   

{_ Report nearest station for pending claims }
{if claim_count > 0:
    Comandante, tiene bonos y recompensas en este sistema para {claim_count} facción{if claim_count > 1:es},
    por un total de {Humanise(system_claims)} créditos.

