    |else:
        {set cur to 0}
        {while cur < len(minordamage):
            {if cur = 0:
                {minordamage[cur].name}
            |elif cur < len(minordamage) - 1:
                , {minordamage[cur].name}
            |else:
                , y {minordamage[cur].name}
            }
            {set cur to cur + 1}
        }
    }.
}

