    {for cmdr in event.commanders:
        {if find(cmdr, "$ShipName") >= 0:
           {_ Ignore NPC ships _}        
        |else: 
           {set commanders to cat(commanders, [cmdr])}
        }
    }

    {ShipName()} ha sido

    {OneOf("convertida en polvo", "eliminada", "destruida")}

    {if len(commanders) = 1:
        por {commanders[0]} 
        en una {event.ships[0]} 
        con rango de combate {event.ratings[0]}.
    |elif len(commanders) = 2:
        por {commanders[0]} 
        en una {event.ships[0]}
        con rango de combate {event.ratings[0]}, 
        y por {commanders[1]} 
        en una {event.ships[1]}
        con rango de combate {event.ratings[1]}.
    |elif len(commanders) > 2:
        {set cur to 0}
        por
        {while cur < len(commanders):
            {if cur = 0:
                {commanders[cur]} 
                en una {event.ships[cur]} 
                con rango de combate {event.ratings[cur]}
            |elif cur < len(commanders) - 1:
                , {commanders[cur]} 
                en una {event.ships[cur]} 
                con rango de combate {event.ratings[cur]}
            |else:
                , y {commanders[cur]} 
                en una {event.ships[cur]} 
                con rango de combate {event.ratings[cur]}.
            }
            {set cur to cur + 1}
        }
    }
}. Activada cápsula de escápe. Emitiendo señal de socorro. {OneOf("Pronto... vendrán a buscarnos","")}.

