Entered normal space.script:
{_ Entered normal space }
{_ Triggered when your ship enters normal space }


{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'disengage')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_body_name', event.body)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.system)}

{set station_notifications to:
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}
    {if station_missions || station_crimes:
        {_ Pause for station ATC before speaking again _}
        {Pause(5000)}
        Información:
        {if station_missions:
            {Pause(500)}
            {station_missions}
        }
        {if station_crimes:
            {Pause(500)}
            {station_crimes}
        }
    }
}

{Pause(500)}

{OneOf("{ShipName()} está ", "", "Estamos ")} 
{OneOf("saliendo de supercrucero {if event.bodytype = \"Anillo planetario\":en el\}", "{OneOf(\"entrando en\", \"volviendo a\", \"regresando a\")} espacio normal {if event.bodytype = \"Anillo planetario\":del\}")} 

{if event.bodytype = 'Planet':
    {OneOf("en órbita de", "cerca de", "en las proximidades de")}
    {set time to SecondsSince(0) - state.eddi_context_approach_time}
    {if time < 60:
        {set settlement to state.eddi_context_last_settlement}
        {set station to StationDetails(settlement, event.system)}
        {if station:
            {SetState('eddi_context_body_name', settlement)}
            {if station.Model.invariantName != "Desconocida":
                {P(station.model)},
            } 
            {P(settlement)}
            {station_notifications}
        |else:
            {P(settlement)}
        }
    |else:
        planet {P(event.body)}
    }
|elif event.bodytype = 'Estrella':
    {OneOf("en órbita", "cerca", "en las proximidades")} de la estrella {P(event.body)}
|elif event.bodytype = 'Estación':
    {set station to StationDetails(event.body, event.system)}
    {OneOf("cerca del espacio aéreo del", "cerca del", "en las proximidades del")}
    {P(station.model)}, {P(event.body)}
    {station_notifications}
|elif event.bodytype = 'Anillo planetario':
   Anillo planetario de {P(token(event.body," Ring",0))}
}.

