Entered signal source.script:
{set warninglevel to:
   {set entered to OneOf("Hemos entrado en una señal", "Entramos en una señal", "Entrando en una señal")}

   {if event.threat < 2:
      {entered}
   |elif event.threat = 2:
      Precaución. {entered} de amenaza media
   |elif event.threat = 3:
      Alerta. {entered} de alta amenaza
   |elif event.threat = 4:
      Peligro. {entered} de muy alta amenaza
   |elif event.threat = 5:
      Peligro extremo. {Occasionally(2, entered)} de muy alta amenaza
   |elif event.threat = 6:
      Peligro extremo. {Occasionally(2, entered)} de amenaza extremadamente alta
   |elif event.threat = 7:
      Peligro extremo. {Occasionally(2, entered)} de ultra alta amenaza
   |else:
      Peligro extremo. {Occasionally(2, entered)} de nivel {event.threat} de amenaza sin precedentes
   }
}

{set source to token(event.source, "$USS_Type_", 1)}
{if source = "Aftermath;":
   {set source to OneOf("procedente de las consecuencias de un combate", "procedente de los restos de una batalla")}
|elif ((source = "Anomaly;" || source = "Salvage;") && event.threat = 2):
   {set warninglevel to entered}
   {set source to OneOf("de origen alienínega", "de procedencia no humana", "de una sonda targoide")}
|elif (source = "Anomaly;" && event.threat = 4):
   {set warninglevel to entered}
   {set source to OneOf("de origen alienígena", "de procedencia no humana", "de un sensor targoide")}
|elif source = "Anomaly;":
   {set source to OneOf("de origen anómalo", "de procedencia desconocida", "de procedencia misteriosa")}
|elif source = "Ceremonial;":
    {set source to "procedente de comunicaciones ceremoniales"}
|elif source = "Convoy;":
    {set source to "procedente de un convoy"}
|elif source = "DisruptedWakeEchoes;":
    {set source to ""}
|elif source = "DistressSignal;":
   {set source to "procedente de un desastre"}
|elif source = "MissionTarget;":
   {set source to OneOf("procedente de una misión", "codificada en una misión")}
|elif source = "NonHuman;":
   {set source to OneOf("de origen alienígena","de procedencia no humana")}
|elif source = "Salvage;":
    {set source to "procedente de transmisiones deterioradas"}
|elif source = "ValuableSalvage;":
    {set source to "procedente de emisiones codificadas"}
|elif source = "VeryValuableSalvage;":
    {set source to "procedente de emisiones de alto grado"}
|elif source = "WeaponsFire;":
   {set source to OneOf("procedente de un combate", "originada por disparo de armas")}
}

{Pause(2000)}
{warninglevel} {source}. 

{if system.state = "Anarchy" && source = "procedente de transmisiones deterioradas" && system.population > 0: 
   {Occasionally(2, "
      Precaución.
      {set local to OneOf('locales', 'cercanas')}
      {set chatter to OneOf('comentarios', 'chismorreos', 'rumores')}
      {set indicate to OneOf('indican', 'sugieren', 'implican', 'informan')}
      {OneOf('transmisiones {local} {indicate}', 
         '{chatter} en transmisiones {local} {indicate}')}
      {Occasionally(3, 'que hay')}
      {OneOf('elevada', 'alta', 'considerable')}
      {OneOf('actividad de', 'presencia de')}
      {OneOf('{Occasionally(2, \'sindicatos\')} criminales ', 
             '{Occasionally(2, OneOf(\'bandas\', \'clanes\'))} piratas ' )}
      en {OneOf('el área', 'los alrededores', 'este sector', 'este sistema')}.
      {OneOf('Permanezca', 'esté', 'manténgase')} {OneOf('vigilante', 'alerta', 'atento')} 
      {Occasionally(3,' {F(\'Honorific\')}' )}.
   ")}
}

