Fuel check.script:
{_ Fuel Check _}
{_ Report fuel level _}


{for compartment in ship.compartments:
    {if compartment.module.basename = "FuelScoop":
        {set scoop_present to true}
    }
}

{set shipsfuel to:
    {OneOf("Niveles", "Depósito", "Reservas")} de combustible
    {Occasionally(2, "ahora")} 
}

{set currentfuel to round(status.fuel_percent, 0)}
{set currentpercent to:
    al {currentfuel} por ciento {Occasionally(2, "de su capacidad")}
}

{set title to Occasionally(3,"{F('Honorific')}")}

{set maxjump to status.fuel/ship.maxfuel}

{if state.eddi_context_last_subject = "jump" && state.eddi_context_last_action = "complete" && maxjump < 1.25:
    Peligro: {shipsfuel} al mínimo.
    {Pause(500)}

    {if scoop_present:
        {if system.scoopable:
            {set scoop_system to system.systemname}
        |else:
            {set detail to JumpDetails('total')}
            {set scoop_system to RouteDetails('scoop', detail.distance)}
            {set scoop_distance to Distance(scoop_system)}
        }

        {if scoop_system && scoop_system != "":
        	{if len(scoopable_bodies) > 1: {set s to "s"}}
            La{s} estrella{s} recolectable más cercana{s} 
            {if scoop_system = system.name:
                {set scoopable_bodies to []}
                {for body in reportSystem.bodies:
                    {if body.scoopable:
                        {set scoopable_bodies to cat(scoopable_bodies, [body.shortname])}
                    }
                }
                del sistema actual
                {if len(scoopable_bodies) > 1:
                    son: 
                    {List(scoopable_bodies)}
                |else:
                    es:
                    {scoopable_bodies[0]}

                }
                {Pause(50)}
                .
            |else:
                en el sistema {scoop_system}, está a {round(scoop_distance, 1)} años luz de aquí.
            }
            {Pause(500)}
            {if maxjump < 0.5:
                 El reabastecimiento {Occasionally(2, "de combustible")} es urgente, {title}.
            |else:
                El reabastecimiento {Occasionally(2, "de combustible")} es altamente recomendable, {title}.
            }
        |else:
            Atención: No hay estrellas recolectables en el rango del próximo salto.
            {Pause(500)}
            Se recomienda el cierre de sistemas no esenciales.
            {Pause(500)}
            Transpondedor de emergencia a la espera, {title}.
        }
    |else:
        {if maxjump < 0.5:
            {OneOf("Es imperativo el reabastecimiento de combustible", "Reabastezca el combustible urgentemente")} {title}.
        |else:
            {OneOf("Por favor, haga un repostaje de combustible cuanto antes", "Se recomienda encarecidamente que recargue combustible")} {title}.
        }
    }
|elif currentfuel < 25:
    {OneOf("Peligro", "Alerta", "Atención")}{title}:
    {shipsfuel} {OneOf("por debajo del 25%", "{currentpercent}" )}.
|elif currentfuel < 50:
    {shipsfuel} {OneOf("por debajo del 50%", "{currentpercent}" )}.
|elif currentfuel < 75:
    {Occasionally(2, "{shipsfuel} {currentpercent}.")}
|elif currentfuel < 100:
    {Occasionally(3, "{shipsfuel} {currentpercent}.")}
|else:
    {ShipName()} {shipsfuel} {OneOf(currentpercent, "al máximo")}.
}

