Jumped.script:
{_ Jumped }
{_ Triggered when you complete a jump to another system }


{_ Context }
{SetState('eddi_context_last_subject', 'jump')}
{SetState('eddi_context_last_action', 'complete')}
{SetState('eddi_context_system_name', system.name)}
{SetState('eddi_context_system_system', system.name)}
{SetState('eddi_context_fuel_remaining', event.fuelremaining)}
{SetState('eddi_context_fuel_used', event.fuelused)}
{SetState('eddi_context_wanted_list', "")}

{set jump_count to state.eddi_context_remaining_jumps}
{if jump_count > 20:
    {set occasional to ceil(jump_count / 10)}
|elif jump_count > 5:
    {set occasional to 2}
|else:
    {set occasional to 1}
}

{if destinationsystem && destinationsystem.name != "":
    {if destinationdistance = 0:
        {ShipName()} ha llegado al sistema {P(destinationsystem.name)}.
        {Pause(1000)}
        {set next_system to RouteDetails("update")}
    |else:
        {if rand(occasional) = 0:
            La distancia a {P(destinationsystem.name)} es de {round(destinationdistance, 1)} años luz
            {if jump_count > 0:
                , a {jump_count} {if jump_count = 1: salto |else: saltos}
                {OneOf("de distancia", "para llegar", "de su destino")}
            }.
        }
    }
|else:
    {if  jump_count > 0 && rand(occasional) = 0:
        {if jump_count = 1: "Queda" |else: "Quedan"} {jump_count} {if jump_count = 1: salto |else: saltos} 
        {OneOf("para llegar", "", "de trayecto", "hasta llegar a nuestro destino")}.
    }
}

{F('Fuel check')}

{_ Report faction states only if it's been more than an hour (3600 seconds) since our last visit. _}
{if SecondsSince(state.eddi_context_system_lastvisit) / 3600 > 1:
    {set state_report to F("System state report")}
}

{set system_missions to F("Mission check system")}
{set system_crimes to F("Crime check system")}

{if state_report || system_missions || (system_crimes && find(system_crimes, "Atención") < 0):
    {Pause(500)}
    Información:
}

{if state_report:
    {Pause(500)}
    {state_report}
}
{if system_missions:
    {Pause(500)}
    {system_missions}
}
{if system_crimes:
    {Pause(500)}
    {system_crimes}
}

