Mission abandoned.script:
{_ Context }
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'abandon')}

{set mission to MissionDetails(event.missionid)}

{if event.communal:
    Ha abandonado un objetivo de comunidad: {mission.localisedname}
|else:
    {if find(mission.typeEDName, "de pasajeros") > -1:
        {set type to "de transporte"}
        {set type to cat(type, mission.passengertype)}
    |else:
        {set type to mission.type}
    }
    {set type to cat("misión ", type)}

{if type = "Hackeo": {set type to "jaqueo"}}

    {type} {if mission.faction: de {mission.faction}} abandonada.
}

