Mission accepted.script:
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'accepted')}

{set mission to MissionDetails(event.missionid)}

{if mission.type = "Donation":
    {OneOf("Has escogido","misión aceptada para")} {Occasionally(2,"generosamente")} donar
    {if mission.typeEDName = "Altruism": {mission.commodity} |else: créditos} para 
    {OneOf('mejorar','levantar','incrementar')} su {OneOf('reputación','estatus')} con {P(mission.faction)}
|elif event.communal:
    Ha aceptado el objetivo de comunidad: {mission.localisedname}
|else:
    {if find(mission.typeEDName, "Passenger") > -1:
        {set type to "transporte de "}
        {set type to cat(type, mission.passengertype)}
    |else:
        {set type to mission.type}
    }
    {if type = "Hackeo": {set type to "jaqueo"}}
    {set type to cat("misión de ",type)}

    {OneOf("{type} {if mission.faction: para {mission.faction}} aceptada",
        "{type} aceptada {if mission.faction: para {mission.faction}}",
        "Ha aceptado la {type} {if mission.faction: para {mission.faction}}")
    }
}

{F("Mission faction state")}

{if mission.passengerswanted:
