 |elif mission.type = "Hackeo": {set type to "jaqueo"}
 |else:
        {set type to mission.type}
        {if mission.type:{set type to cat("misión de ",type)}
         |else: {set type to "misión"}}
}


{set ranktype to token(mission.name,"_",2)}

{if type = "misión de " || type = "misión":
    {set m to "a"}
|else:
    {set m to "o"}
}

{if type = "misión de " || type = "misión":
    {set la to "la"}
|else:
    {set la to "el"}
}

{if mission.type = "Donativo":
    {if mission.typeEDName = "Altruism":
        {set donate to cat(event.amount, " toneladas de ",event.commodity)}
    |else:

{if event.donation > 1300000: {set esde to "es de"}}
{if event.donation <= 1300000: {set esde to " de"}}
{if event.donation < 1000000: {set esde to ""}}

        {set donate to cat(Humanise(event.donation),"{esde} créditos")}
    }

    {OneOf("Ha hecho un donativo de {Occasionally(2, '{donate}')} a la {Occasionally(2, 'agradecida')} facción {P(mission.faction)}",
        "{P(mission.faction)} ha recibido su donación {Occasionally(2, 'de {donate}')}",
        "{P(mission.faction)} le da las gracias por su donación {Occasionally(2, 'de {donate}')}")}
    {F("Mission faction state")}.

|elif ranktype = "RANKEMP" || ranktype = "RankEmp" || ranktype = "RANKFED" || ranktype = "RankFed":
    {OneOf("Ha completado la misión de {type} satisfactoriamente","La misión de {type} se ha completado satisfactoriamente")}
|elif mission.communal:
    Ha completado el objetivo de comunidad: {mission.name}.

{if mission.reward > 1300000: {set esde to "es de"}}
{if mission.reward <= 1300000: {set esde to " de"}}
{if mission.reward < 1000000: {set esde to ""}}

    Ha {OneOf('recibido','sido recompensad{o} con')} {Humanise(mission.reward)}{esde} créditos por su
    {OneOf("contribución","participación","esfuerzo")}
|else:
    {if event.reward > 0:

{if event.reward > 1300000: {set esde to "es de"}}
{if event.reward <= 1300000: {set esde to " de"}}
{if event.reward < 1000000: {set esde to ""}}

