Missions route.script:
{_ Missions route }
{_ Get the missions route for various mission details }


{if event.routetype = "expiring":
     {if event.distance > 0:
         {set mission to MissionDetails(event.missionids[0])}

         {if find(mission.typeEDName, "Passenger") > -1:
             {set type to "Transport "}
             {set type to cat(type, mission.passengertype)}
         |else:
             {set type to mission.type}
         }
         {set type to cat(type, " misión")}

         {type} {if mission.faction: for {mission.faction}}
         {if mission.destinationsystem: in the
             {if mission.destinationsystem = system.name:
                 actual
             |else:
                 {mission.destinationsystem}
             } sistema
         } está apunto de expirar.
     |else:
         No hay misiones activas.
     }
|elif event.routetype = "farthest":
     {if event.distance > 0:
         El {if missionsCount = 1: único objetivo de la misión |else: objetivo de la misión más lejano} está en
          el
         {if event.system = system.name:
             sistema actual
         |else:
             sistema {event.system}, a {round(event.distance, 1)} años luz de {OneOf("distancia","aquí")}
         }.
     |else:
         Misión no encontrada con un sistema de destino designado.
     }
|elif event.routetype = "most":
     {if missionsCount = 1:
         El único objetivo de misión que tenemos está {Occasionally(2,"localizado")} en
         {if missions[0].destinationsystem = "":
             un sistema {Occasionally(2,"estelar")} desconocido
         |elif missions[0].destinationsystem = system.name:
             el sistema {Occasionally(2,"estelar")} actual
         |else:
             el sistema {Occasionally(2,"estelar")} {missions[0].destinationsystem}
         }
         .
     |else:
         {set systems to split(event.systems, "_")}
         {set system_most to event.system}
         {set systems_count to len(systems)}

         {if systems_count = 1:
             {if system_most = system.name:
                 {set system_most to "el sistema actual"}
             |else:
                 {set system_most to cat("El ", system_most, " sistema")}
             }
             {OneOf("La mayoría de las misiones están {Occasionally(2,'localizadas')} en {system_most}",
                 "{system_most} tiene {OneOf('más','la mayoría de las')} misiones",
                 "{system_most} tiene la mayoría de las misiones para completar")}.
         |elif systems_count > 1:
             Tenemos {event.count} objetivo{if event.count > 1:s} en cada uno, en los sistemas

             {set cur to 0}
             {while cur < len(equal):
                 {systems[cur]}
                 {set cur to cur + 1}
                 {if cur = systems_count-1: y |elif cur < systems_count: ,}
             }.
         }
     }
|elif event.routetype = "nearest":
     {if event.distance > 0:
         El {if missionsCount = 1: único objetivo|else: objetivo más cercano}
         está en el sistema
         {if event.system = system.name:
             actual
         |else:
             {event.system}, a {round(event.distance, 1)} años luz de {OneOf("distancia","aquí")}
         }.
     |else:
         No se encuentra un sistema de destino designado.
     }
|elif event.routetype = "route":
     {if event.routedistance > 0:
         Calculada ruta de misiones para {event.count} sistemas.
         La distancia total de la ruta es de {round(event.routedistance, 1)} años luz.
         El primer destino es el sistema {event.system}, a {round(event.distance, 1)} años luz de {OneOf("distancia","aquí")}.
     |else:
         Imposible calcular una ruta de misiones.
     }
|elif event.routetype = "source":
     {if event.count > 0:
         {set systems to split(event.route, "_")}

         {set pos to 0}
         {while pos < event.count:

             {set source_cargo to []}
             {for missionid in event.missionids:
                 {set haulage to HaulageDetails(missionid)}
                 {if haulage.sourcesystem = systems[pos]:
                     {set cargo to CargoDetails(missionid)}
                     {if find(source_cargo, cargo.name) = -1:
                         {set source_cargo to cat(source_cargo, [cargo.name])}
                     }
                 }
             }

             {if pos = 0:
                 El {if event.count = 1: único cargamento |else: cargamento más cercano}
             |elif pos = 1:
                 Además, el cargamento
             |elif pos = event.count - 1:
                 finalmente, el cargamento
             |else:
                 el cargamento
             }

             
             {set cur to 0}
             {while cur < len(source_cargo):
                 {source_cargo[cur]}
                 {set cur to cur + 1}
                 {if cur = len(source_cargo) - 1: y |elif cur < len(source_cargo): ,}
             }

             está en
             {if systems[pos] = system.name:
                 el sistema actual
             |else:
                 el sistema {systems[pos]}, a
                 {set distance to Distance(system.name, systems[pos])}
                 {round(distance, 1)} años luz de {OneOf("distancia","aquí")}.
             }.
             {set pos to pos + 1}
         }
         {SetState('eddi_context_cargo_source', event.route)}
     |else:
         Cargamento con destino desconocido.
     }
|elif event.routetype = "update":
     {if event.routedistance > 0:
         Next mission destination is {event.system}, {round(event.distance, 1)} lightyears away.
     |else:
         No consigo actualizar el siguiente destino de la misión.
     }
}

