{set systemsrepaired to []}

{if event.hull:
   {set systemsrepaired to cat(systemsrepaired, ["el casco"])} 
}
{if event.cockpit:
   {set systemsrepaired to cat(systemsrepaired, ["la cabina"])} 
}
{if event.corrosion:
   {set systemsrepaired to cat(systemsrepaired, [OneOf("sistemas corroidos", "sistemas afectados por corrosión")])} 
}

{if len(systemsrepaired) > 0:

    {set remote to Occasionally(2, "remotas")}
    {set sequence to OneOf("el drón ha", "Se han")} 
