    )}

    {set cur to 0}
    {while cur < len(systemsrepaired):
        {if cur = 0:
        |elif cur < len(systemsrepaired) - 1:
            , 
        |else:
            and
        }
        {systemsrepaired[cur]}
        {set cur to cur + 1}
    }
}.

