Route details.script:
{_ Route details }
{_ Triggered when a route has been generated or updated }


{if event.routetype = "expiring":
     {if event.distance > 0:
         {set mission to MissionDetails(event.missionids[0])}

         {if find(mission.typeEDName, "Passenger") > -1:
             {set type to "Transporte "}
             {set type to cat(type, mission.passengertype)}
         |else:
             {set type to mission.type}
         }
         {set type to cat(type, " misión")}

         {type} {if mission.faction: para {mission.faction}}
         {if mission.destinationsystem: en la
             {if mission.destinationsystem = system.name:
                 actual
             |else:
                 {mission.destinationsystem}
             } sistema
         } is cerca de expirar.
     |else:
         No tiene misiones activas.
     }
|elif event.routetype = "facilitator":
    {if event.distance > 0:
        Nuestro contacto de Interestélar fáctor más cercano está en
        la estación {destinationstation.name}, en el sistema
        {if event.system = system.name:
            este sistema
        |else:
            sistema {event.system}, a {round(event.distance, 1)} años luz de aquí
        }.
     |else:
         No se encuenta ningún contacto de Interestélar fáctor en el rango del sensor.
     }
|elif event.routetype = "farthest":
     {if event.distance > 0:
         Su {if missionsCount = 1: único |else: más lejano}
         objetivo está en el sistema
         {if event.system = system.name:
             este sistema
         |else:
             sistema {event.system}, a {round(event.distance, 1)} años luz de aquí
         }.
     |else:
         No se ha encontrado ninguna misión con un sistema destino designado.
     }
|elif event.routetype = "most":
     {if missionsCount = 1:
         Su único objeto está {Occasionally(2,"localizado")} en
         {if missions[0].destinationsystem = "":
             un sistema {Occasionally(2,"estelar")} desconocido
         |elif missions[0].destinationsystem = system.name:
             el actual sistema {Occasionally(2,"estelar")}
         |else:
             el sistema {Occasionally(2,"estelar")} {missions[0].destinationsystem}
         }
         .
     |else:
         {set systems to split(event.systems, "_")}
         {set system_most to event.system}
         {set systems_count to len(systems)}

         {if systems_count = 1:
             {if system_most = system.name:
                 {set system_most to "El sistema actual"}
             |else:
                 {set system_most to cat("el sistema ", system_most)}
             }
             {OneOf("La mayoría de sus misiones están {Occasionally(2,'localizadas')} en {system_most}",
                 "{system_most} tiene {OneOf('la mayoría','la mayoría de sus')} misiones",
                 "{system_most} la mayoría de misiones pendientes de completar")}.
         |elif systems_count > 1:
             Tienes {event.count} objetivo{if event.count > 1:s, cada uno} en {if event.count > 1:los sistemas|else:el sistema}

             {set cur to 0}
             {while cur < len(equal):
                 {systems[cur]}
                 {set cur to cur + 1}
                 {if cur = systems_count-1: y |elif cur < systems_count: ,}
             }.
         }
     }
|elif event.routetype = "nearest":
     {if event.distance > 0:
         Su {if missionsCount = 1: único objetivo |else: objetivo más cercano}
         está en el
         {if event.system = system.name:
             sistema actual
         |else:
             sistema {event.system}, a {round(event.distance, 1)} años luz de aquí
         }.
     |else:
         No se ha encontrado ninguna misión con un sistema destino designado.
     }
|elif event.routetype = "route":
     {if event.routedistance > 0:
         Ruta de misiones calculada para {event.count} sistemas.
         La distancia total de la ruta es de {round(event.routedistance, 1)} años luz.
         El primer objeto está en el sistema {event.system}, a {round(event.distance, 1)} de aquí.
     |else:
         No consigo calcular la ruta para las misiones actuales.
     }
|elif event.routetype = "source":
     {if event.count > 0:
         {set systems to split(event.route, "_")}

         {set pos to 0}
         {while pos < event.count:

             {set source_cargo to []}
             {for missionid in event.missionids:
                 {set haulage to HaulageDetails(missionid)}
                 {if haulage.sourcesystem = systems[pos]:
                     {set cargo to CargoDetails(missionid)}
                     {if find(source_cargo, cargo.name) = -1:
                         {set source_cargo to cat(source_cargo, [cargo.name])}
                     }
                 }
             }

             {if pos = 0:
                 El origen de su {if event.count = 1: única misión de carga |else: misión de carga más cercana}
             |elif pos = 1:
                 además, El origen de su misión de carga
             |elif pos = event.count - 1:
                 finalmente, El origen de su misión de carga
             |else:
                 Su misión de carga
             }

             para
             {set cur to 0}
             {while cur < len(source_cargo):
                 {source_cargo[cur]}
                 {set cur to cur + 1}
                 {if cur = len(source_cargo) - 1: y |elif cur < len(source_cargo): ,}
             }

             está en el sistema 
             {if systems[pos] = system.name:
                 actual
             |else:
                 {systems[pos]},
                 {set distance to Distance(system.name, systems[pos])}
                 a {round(distance, 1)} años luz de aquí
             }.
             {set pos to pos + 1}
         }
         {SetState('eddi_context_cargo_source', event.route)}
     |else:
         No se ha encontrado ningún origen de carga con un sistema de destino designado
     }
|elif event.routetype = "update":
     {if event.system && event.system != "":
         El destino de la siguiente misión es el sistema {event.system}, a {round(event.distance, 1)} años luz de aquí.
     }
}

