Star report.script:
{_ Preferences _}
{set reportAge to 0}              {_ Set to 1 if you want to hear about the age of the star. _}
{set reportChromaticity to 0}     {_ Set to 1 if you want to hear about the colour of the star. _}
{set reportNotables to 1}         {_ Set to 0 if you don't want to hear about noteworthy main sequence star features. _}
{set reportScanValue to 0}        {_ Set to 1 if you want scan value reported _}
{set reportHabZone to 0}          {_ Set to 1 if you want habitable zone details for the primary star_}
{set mainSequenceFrequency to 2}  {_ Set to determine the frequency (1 chance in n) for describing stars as "main sequence" _}

{_ Fetch from context }
{if event: {set reportbody to event} }
{if !event: {set reportbody to BodyDetails(state.eddi_context_star_star, state.eddi_context_star_system)}}

{if !reportbody.name || reportbody.name = "":

    No consigo saber a que estrella se refiere.

|else:

    {set mainsequence to ["O", "B", "A", "F", "G", "K", "M"]}
    {for starclass in mainsequence:
        {if reportbody.stellarclass = starclass:
            {set mainsequence to 1}
        }
    }    

    {if mainsequence = 1 && reportNotables = 1:

        {set notables to []}
        {if reportbody.massprobability < 0.01:
            {set notables to cat(notables, [cat("una masa solar increíblemente baja de ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability < 0.1:
            {set notables to cat(notables, [cat("una masa solar extremadamente baja de ", Humanise(reportbody.solarmass))])}
        |elif reportbody.massprobability < 1:
            {set notables to cat(notables, ["una masa solar inusualmente baja"])}
        |elif reportbody.massprobability < 10:
            {set notables to cat(notables, ["menor que la masa solar promedio"])}
        |elif reportbody.massprobability > 99.99:
