        {signal.source}}
    	
    }
    
    {_ detección de plural y género}

{if signal.amount > 1: {set s to "s"} 
|else: {set s to ""}
}
    
{set surfacesignals to cat(surfacesignals, [surfacesignals_desc])}

}


