System state changed.script:
{_ Context }
{SetState('eddi_context_last_subject', 'system')}
{SetState('eddi_context_last_action', 'change_state')}
{SetState('eddi_context_system', event.system)}
{SetState('eddi_context_system_system', event.system)}

{_ Set up our system name}
{if event.system = homesystem.name:
  {set system to OneOf('{P(event.system)}',
                       'Tu sistema hogar')}
|elif event.system = system.name:
  {set system to OneOf('{P{event.system)}',
                       'este sistema')}
|else:
  {set system to OneOf('sistema {P(event.system)}'
                       '{P(event.system)}'))}
}

{if event.newstate != 'None':
  {_ System has entered a new state}
  {OneOf('Las transmisiones locales informan que', 'Información: ', 'Transmisión interceptada. Informan que ')}
  {if event.newstate = 'Boom':
    {OneOf('{system} está experimentando un auge comercial',
           '{system} está entrando en un período de mayor éxito comercial')}
  |elif event.newstate = 'Bust':
    {system} está experimentando dificultades económicas
  |elif event.newstate = 'Civil Unrest':
    {OneOf('{system} está experimentando disturbios civiles',
           'hay disturbios en el {system} system')}
  |elif event.newstate = 'Civil War':
    {OneOf('{system} está en guerra con otra facción en el mismo sistema',
           'una guerra civil estalló en {system}',
           '{system} ha caído en una guerra civil',
           'han estallado hostilidades en {system}')}
  |elif event.newstate = 'Election':
    {system} está pasando por elecciones
  |elif event.newstate = 'Expansion':
    {OneOf("una facción en {system} está intentando expandirse a un sistema vecino",
           "una facción en {system} está preparándose para expandirse a un sistema vecino")}
  |elif event.newstate = 'Famine':
    {system} está sufriendo hambruna
  |elif event.newstate = 'Investment':
    En {system} la facción controladora está invirtiendo fuertemente en su infraestructura local
  |elif event.newstate = 'Lockdown':
    {system} ha aumentado la seguridad debido a un bloqueo por parte de la facción dominante
  |elif event.newstate = 'Outbreak':
    {system} sufre de un brote de enfermedad contagiosa
  |elif event.newstate = 'Retreat':
    La facción que controla {system} se está retirando de un sistema vecino
  |elif event.newstate = 'War':
    {system} ahora está en guerra con un sistema vecino
  |else:
   el {OneOf('{system} ha entrado en {event.state}',
           '{system} ahora está en {event.state}')}
  }.
}

